import java.io.File fun main(): Unit = println(mapOf('A' to 1, 'B' to 2, 'C' to 3, 'X' to 1, 'Y' to 2, 'Z' to 3).let { k -> File("input.txt").readLines().let { if(System.getenv("part") != "part2") it.map { inp -> when ((k[inp.toCharArray()[2]]!!) - (k[inp.toCharArray()[0]]!!)) { 0 -> 3 + k[inp.toCharArray()[2]]!!; -2, 1 -> 6 + k[inp.toCharArray()[2]]!!; else -> 0 + k[inp.toCharArray()[2]]!! } } else it.map { inp -> ((k[inp.toCharArray()[2]]!! - 1)*3 + 1 + Math.floorMod(k[inp.toCharArray()[0]]!! - 1 + (k[inp.toCharArray()[2]]!!-2), 3)) } } }.sum())