import java.io.File fun main(): Unit = println(File("input.txt").readLines().let { if(System.getenv("part") != "part2") it.map { inp -> (inp.take(inp.length / 2).toCharArray().distinct().intersect(inp.drop(inp.length / 2).toCharArray().distinct().toSet())).first()}.sumOf { l -> if (l.isLowerCase()) l.code - 96 else l.code - 38 } else { it.windowed(3, step = 3).map { w -> w.map { it.toSet() }.let { c -> c[0].intersect(c[1]).intersect(c[2]) }.first() } .sumOf { l ->  if (l.isLowerCase()) l.code - 96 else l.code - 38  } } })